import React, { useState, useEffect } from 'react';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [showModal, setShowModal] = useState(false);

  // Toggle Dark Mode
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <div className="bg-gray-100 dark:bg-gray-900 min-h-screen transition duration-500">
      <nav className="bg-white dark:bg-gray-800 shadow-md p-4">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold text-gray-800 dark:text-gray-200">
            üöÄ Trusty Mini App
          </h1>
          <div>
            <button
              onClick={() => setShowModal(true)}
              className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition duration-300 mr-4"
            >
              ‚úçÔ∏è Leave a Review
            </button>
            <button
              onClick={() => setDarkMode(!darkMode)}
              className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition duration-300"
            >
              {darkMode ? 'üåû Light Mode' : 'üåô Dark Mode'}
            </button>
          </div>
        </div>
      </nav>

      <main className="max-w-7xl mx-auto p-4">
        <section className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg mt-6 transition duration-500">
          <h2 className="text-3xl font-bold text-gray-800 dark:text-gray-200">
            Top Reviews
          </h2>
          <p className="mt-4 text-gray-600 dark:text-gray-400">
            Check out the top-rated reviews from our community!
          </p>
          <div className="mt-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg shadow hover:shadow-lg transition duration-300">
              <h3 className="text-xl font-bold text-gray-700 dark:text-gray-300">John Doe</h3>
              <p className="text-gray-600 dark:text-gray-400">"This is the best app ever!"</p>
            </div>
            <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg shadow hover:shadow-lg transition duration-300">
              <h3 className="text-xl font-bold text-gray-700 dark:text-gray-300">Jane Smith</h3>
              <p className="text-gray-600 dark:text-gray-400">"I love using this platform daily."</p>
            </div>
            <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg shadow hover:shadow-lg transition duration-300">
              <h3 className="text-xl font-bold text-gray-700 dark:text-gray-300">Alice Johnson</h3>
              <p className="text-gray-600 dark:text-gray-400">"Great features and easy to use."</p>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}

export default App;
