import React, { useState, useEffect } from 'react';
import { FaMoon, FaSun, FaStar } from 'react-icons/fa';
import toast, { Toaster } from 'react-hot-toast';

function App() {
  const [darkMode, setDarkMode] = useState(
    localStorage.getItem('darkMode') === 'true'
  );
  const [showModal, setShowModal] = useState(false);
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState('');
  const [rating, setRating] = useState(0);
  const [anonymous, setAnonymous] = useState(false);
  const [hideProfilePic, setHideProfilePic] = useState(false);
  const [hideUsername, setHideUsername] = useState(false);
  const [hideName, setHideName] = useState(false);

  useEffect(() => {
    document.documentElement.classList.toggle('dark', darkMode);
    localStorage.setItem('darkMode', darkMode);
  }, [darkMode]);

  const censorText = (text) => {
    return text.replace(/([a-zA-Z])/g, (char, index) => (index % 2 === 0 ? char : '*'));
  };

  const handleSubmitReview = () => {
    if (rating === 0) {
      toast.error("Oops! Looks like you forgot to give a star rating.", { position: "top-center" });
      return;
    }
    if (newReview.trim() === '') {
      toast.error("Please enter your review before submitting.", { position: "top-center" });
      return;
    }
    if (anonymous && !hideProfilePic && !hideUsername && !hideName) {
      toast.error("Please select at least one privacy option when posting anonymously.", { position: "top-center" });
      return;
    }
    const reviewData = {
      text: newReview,
      stars: rating,
      timestamp: new Date().toLocaleString(),
      user: anonymous
        ? {
            name: hideName ? censorText("David Finch") : "David Finch",
            username: hideUsername ? censorText("@tradeguru") : "@tradeguru",
            profilePic: hideProfilePic ? "https://via.placeholder.com/50" : 'https://via.placeholder.com/50',
          }
        : {
            name: "David Finch",
            username: "@tradeguru",
            profilePic: 'https://via.placeholder.com/50',
          },
    };
    setReviews([...reviews, reviewData]);
    setNewReview('');
    setRating(0);
    setShowModal(false);
    toast.success("Review submitted successfully!");
  };

  return (
    <div className="bg-gray-100 dark:bg-gray-900 min-h-screen flex flex-col items-center justify-center p-4">
      <Toaster position="top-center" />
      <div className="absolute top-4 right-4 cursor-pointer" onClick={() => setDarkMode(!darkMode)}>
        {darkMode ? <FaSun className="text-yellow-500 text-2xl" /> : <FaMoon className="text-gray-700 text-2xl" />}
      </div>

      <h1 className="text-4xl font-bold text-gray-800 dark:text-gray-200 text-center mb-6">Trusty</h1>

      <button 
        className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-6 rounded-lg transition" 
        onClick={() => {
          setShowModal(true);
          setNewReview('');
          setRating(0);
        }}>
        Leave a Review
      </button>

      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg w-full max-w-md">
            <h3 className="text-2xl font-bold text-gray-800 dark:text-gray-200 mb-4">Leave a Review</h3>

            <div className="flex mb-4">
              {[1, 2, 3, 4, 5].map((star) => (
                <FaStar
                  key={star}
                  className={`cursor-pointer text-2xl transition ${
                    star <= rating ? 'text-yellow-500' : 'text-gray-300'
                  }`}
                  onClick={() => setRating(star)}
                />
              ))}
            </div>

            <textarea
              className="w-full p-2 border rounded-md dark:bg-gray-700 dark:text-white"
              placeholder="Write your review here..."
              value={newReview}
              onChange={(e) => setNewReview(e.target.value)}
            ></textarea>

            <div className="flex items-center mt-2">
              <input 
                type="checkbox" 
                id="anonymous" 
                checked={anonymous} 
                onChange={() => setAnonymous(!anonymous)}
                className="mr-2"
              />
              <label htmlFor="anonymous" className="text-gray-700 dark:text-gray-300">Anonymous</label>
            </div>

            {anonymous && (
              <div className="flex flex-col mt-2 text-gray-700 dark:text-gray-300">
                <label><input type="checkbox" checked={hideProfilePic} onChange={() => setHideProfilePic(!hideProfilePic)} /> Hide Profile Picture</label>
                <label><input type="checkbox" checked={hideUsername} onChange={() => setHideUsername(!hideUsername)} /> Hide Username</label>
                <label><input type="checkbox" checked={hideName} onChange={() => setHideName(!hideName)} /> Hide Name</label>
              </div>
            )}

            <div className="flex justify-end mt-4">
              <button 
                className="bg-gray-300 dark:bg-gray-600 text-gray-800 dark:text-gray-200 px-4 py-2 rounded-lg mr-2" 
                onClick={() => setShowModal(false)}>
                Cancel
              </button>
              <button 
                className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg transition" 
                onClick={handleSubmitReview}>
                Submit
              </button>
            </div>
          </div>
        </div>
      )}

      <div className="mt-6 w-full max-w-2xl">
        {reviews.length > 0 ? (
          reviews.map((rev, index) => (
            <div key={index} className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow-lg mb-4">
              <div className="flex items-center mb-2">
                {rev.user.profilePic && <img src={rev.user.profilePic} alt="User" className="w-8 h-8 rounded-full mr-2" />}
                <span className="font-bold text-gray-800 dark:text-gray-200">{rev.user.name}</span>
                <span className="text-sm text-gray-500 dark:text-gray-400 ml-2">{rev.user.username}</span>
              </div>
              <div className="flex items-center mb-1">
                {[...Array(rev.stars)].map((_, i) => <FaStar key={i} className="text-yellow-500" />)}
              </div>
              <p className="text-gray-600 dark:text-gray-300">{rev.text}</p>
              <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">{rev.timestamp}</p>
            </div>
          ))
        ) : (
          <p className="text-gray-500 dark:text-gray-400 mt-4 text-center">No reviews yet. Be the first to leave one!</p>
        )}
      </div>
    </div>
  );
}

export default App;
