import React, { useState, useEffect } from 'react';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [rating, setRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  const [review, setReview] = useState("");
  const [reviews, setReviews] = useState([]);

  // Toggle Dark Mode
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  // Handle Review Submission
  const submitReview = () => {
    if (review.trim() === "" || rating === 0) {
      alert("‚ö†Ô∏è Please provide a star rating and a review before submitting.");
      return;
    }

    const newReview = {
      id: Date.now(),
      rating,
      text: review,
    };

    setReviews([newReview, ...reviews]); // Add new review to list
    setShowModal(false);
    setReview("");
    setRating(0);
  };

  return (
    <div className={`bg-gray-100 dark:bg-gray-900 min-h-screen transition duration-500`}>
      {/* Navigation Bar */}
      <nav className="bg-white dark:bg-gray-800 shadow-md p-4">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold text-gray-800 dark:text-gray-200">
            üöÄ Trusty Mini App
          </h1>
          <div>
            <button
              onClick={() => setShowModal(true)}
              className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition duration-300 mr-4"
            >
              ‚úçÔ∏è Leave a Review
            </button>
            <button
              onClick={() => setDarkMode(!darkMode)}
              className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition duration-300"
            >
              {darkMode ? 'üåû Light Mode' : 'üåô Dark Mode'}
            </button>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto p-4">
        <section className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg mt-6 transition duration-500">
          <h2 className="text-3xl font-bold text-gray-800 dark:text-gray-200">
            Top Reviews
          </h2>
          <p className="mt-4 text-gray-600 dark:text-gray-400">
            Check out the top-rated reviews from our community!
          </p>

          {/* Display Reviews */}
          <div className="mt-6 space-y-4">
            {reviews.length > 0 ? (
              reviews.map((rev) => (
                <div key={rev.id} className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg shadow hover:shadow-lg transition duration-300">
                  <div className="flex items-center">
                    <span className="text-yellow-400 text-xl">
                      {"‚≠ê".repeat(rev.rating)}
                    </span>
                    <span className="ml-2 text-gray-600 dark:text-gray-400">{rev.text}</span>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-500 dark:text-gray-400 mt-4">No reviews yet. Be the first to leave one!</p>
            )}
          </div>
        </section>
      </main>

      {/* Review Modal */}
      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg w-full max-w-md">
            <h3 className="text-2xl font-bold text-gray-800 dark:text-gray-200">
              ‚úçÔ∏è Leave a Review
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mt-2">
              We value your feedback!
            </p>

            {/* Star Rating System with Hover Effect */}
            <div className="flex space-x-2 mt-4">
              {[1, 2, 3, 4, 5].map((star) => (
                <span
                  key={star}
                  className={`text-3xl cursor-pointer ${
                    (hoverRating || rating) >= star ? 'text-yellow-400' : 'text-gray-400'
                  }`}
                  onMouseEnter={() => setHoverRating(star)}
                  onMouseLeave={() => setHoverRating(0)}
                  onClick={() => setRating(star)}
                >
                  ‚≠ê
                </span>
              ))}
            </div>

            {/* Review Input */}
            <textarea
              className="w-full mt-4 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white"
              rows="4"
              placeholder="Share your thoughts..."
              value={review}
              onChange={(e) => setReview(e.target.value)}
            ></textarea>

            {/* Submit Button */}
            <button
              className="mt-4 bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition duration-300"
              onClick={submitReview}
            >
              Submit Review
            </button>

            {/* Cancel Button */}
            <button
              className="mt-2 text-gray-600 dark:text-gray-400 hover:text-gray-800 dark:hover:text-gray-200 transition duration-300"
              onClick={() => setShowModal(false)}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
