import React, { useState, useEffect } from "react";

function App() {
  // Dark Mode State
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("darkMode") === "true";
  });

  // Reviews State
  const [reviews, setReviews] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [review, setReview] = useState("");
  const [rating, setRating] = useState(0);
  const [anonymousReview, setAnonymousReview] = useState(false);

  // Dark Mode Effect
  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  // Submit Review
  const submitReview = () => {
    if (review.trim() === "" || rating === 0) {
      alert("‚ö†Ô∏è Please provide a star rating and a review before submitting.");
      return;
    }

    // Simulating Telegram user data (replace with actual Telegram API)
    const telegramUser = {
      username: "john_doe",
      profilePic: "https://t.me/i/userpic/320/johndoe.jpg",
      isAnonymous: anonymousReview,
    };

    const newReview = {
      id: Date.now(),
      rating,
      text: review,
      username: telegramUser.isAnonymous ? "Anonymous" : `@${telegramUser.username}`,
      profilePic: telegramUser.isAnonymous
        ? "https://cdn-icons-png.flaticon.com/512/149/149071.png"
        : telegramUser.profilePic,
      timestamp: new Date().toLocaleDateString("en-US", {
        year: "numeric",
        month: "short",
        day: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      }),
    };

    setReviews([newReview, ...reviews]);
    setShowModal(false);
    setReview("");
    setRating(0);
    alert("‚úÖ Review Submitted Successfully!");
  };

  return (
    <div className={`min-h-screen ${darkMode ? "dark bg-gray-900" : "bg-gray-100"}`}>
      {/* Navbar */}
      <nav className="bg-white dark:bg-gray-800 p-4 shadow-lg flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-800 dark:text-gray-200">
          üöÄ Trusty Mini App
        </h1>
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="p-2 bg-gray-300 dark:bg-gray-700 text-gray-900 dark:text-white rounded-lg"
        >
          {darkMode ? "üåô Dark Mode" : "‚òÄÔ∏è Light Mode"}
        </button>
      </nav>

      {/* Main Content */}
      <div className="flex flex-col items-center justify-center p-6">
        {/* Review Button */}
        <button
          onClick={() => setShowModal(true)}
          className="mt-6 px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow-lg hover:bg-blue-700 transition duration-300"
        >
          ‚ûï Leave a Review
        </button>

        {/* Review Modal */}
        {showModal && (
          <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
            <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg w-full max-w-md">
              <h3 className="text-2xl font-bold text-gray-800 dark:text-gray-200">Leave a Review</h3>

              {/* Star Rating */}
              <div className="mt-4 flex">
                {[1, 2, 3, 4, 5].map((num) => (
                  <span
                    key={num}
                    className={`text-3xl cursor-pointer ${rating >= num ? "text-yellow-400" : "text-gray-300"}`}
                    onClick={() => setRating(num)}
                  >
                    ‚≠ê
                  </span>
                ))}
              </div>

              {/* Review Text */}
              <textarea
                className="w-full mt-4 p-2 border rounded-lg dark:bg-gray-700 dark:text-white"
                placeholder="Write your review..."
                value={review}
                onChange={(e) => setReview(e.target.value)}
              ></textarea>

              {/* Anonymous Toggle */}
              <div className="flex items-center mt-4">
                <input
                  type="checkbox"
                  id="anonymousReview"
                  className="mr-2"
                  checked={anonymousReview}
                  onChange={() => setAnonymousReview(!anonymousReview)}
                />
                <label htmlFor="anonymousReview" className="text-gray-800 dark:text-gray-200">
                  Post review anonymously
                </label>
              </div>

              {/* Submit & Close Buttons */}
              <div className="flex justify-end mt-4 space-x-2">
                <button
                  onClick={() => setShowModal(false)}
                  className="px-4 py-2 bg-gray-400 text-white rounded-lg hover:bg-gray-500 transition duration-300"
                >
                  Cancel
                </button>
                <button
                  onClick={submitReview}
                  className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition duration-300"
                >
                  Submit
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Display Reviews */}
        <div className="mt-6 space-y-4 w-full max-w-lg">
          {reviews.length > 0 ? (
            reviews.map((rev) => (
              <div
                key={rev.id}
                className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg shadow hover:shadow-lg transition duration-300 flex items-center space-x-4"
              >
                {/* User Profile Picture */}
                <img
                  src={rev.profilePic}
                  alt="Profile"
                  className="w-10 h-10 rounded-full border border-gray-300 dark:border-gray-500"
                />

                {/* Review Content */}
                <div className="flex-1">
                  <div className="flex items-center justify-between">
                    <div>
                      <span className="font-bold text-gray-800 dark:text-gray-200">{rev.username}</span>
                      <span className="text-sm text-gray-500 dark:text-gray-400 ml-2">{rev.timestamp}</span>
                    </div>
                    <span className="text-yellow-400 text-xl">{"‚≠ê".repeat(rev.rating)}</span>
                  </div>
                  <p className="text-gray-600 dark:text-gray-300 mt-2">{rev.text}</p>
                </div>
              </div>
            ))
          ) : (
            <p className="text-gray-500 dark:text-gray-400 mt-4">No reviews yet. Be the first to leave one!</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
